package cobweb

import (
	"fmt"
	"math/rand"
	"strconv"
	"strings"
)

func getWindows() string {
	etc := []string{"WOW64", "Win64; x64"}
	ver := []string{"10.0", "6." + strconv.Itoa(rand.Intn(4))}
	main := "Windows NT "

	version := ver[rand.Intn(1)]

	randBool := rand.Intn(2) != 1
	if version == "10.0" || randBool {
		version += "; " + etc[rand.Intn(2)]
	}

	return main + version
}

func getMacOs() string {
	main := "Macintosh; Intel Mac OS X 11_"
	sub := strconv.Itoa(rand.Intn(5) + 10)
	if sub != "14" {
		sub += "_" + strconv.Itoa(rand.Intn(7))
	} else {
		sub += "_" + strconv.Itoa(rand.Intn(3))
	}

	return main + sub
}

func getLinux() string {
	ver := []string{"x86_64", "i686", "i686 on x86_64"}
	main := "X11; Linux "

	return main + ver[rand.Intn(len(ver))]
}

func getAndroid() string {
	plat := []string{"Android 6.0", "Android 7.1.1", "Android 8.0", "Android 9.0", "Android 10.0"}
	device := []string{"Galaxy Note 3 SM-N900T Build/JSS15J", "Galaxy Note 2 SM-N900T Build/JSS15J", "Galaxy Note 1 SM-N900T Build/JSS15J", "Pixel 2 Build/OPD3.170816.012"}

	main := "Linux; "

	return main + plat[rand.Intn(len(plat))] + "; " + device[rand.Intn(len(device))]
}

func getIphone() string {
	ver := []string{"10", "11", "12", "13", "14"}
	ver2 := []string{"_0", "_1", "_2", "_3", "_4", "_5"}

	return fmt.Sprintf("iPhone; CPU iPhone OS %s%s like Mac OS X", ver[rand.Intn(len(ver))], ver2[rand.Intn(len(ver2))])
}

func getRandomOs(isMobile bool) string {
	var os []func() string
	if isMobile {
		os = []func() string{getAndroid, getIphone}
	} else {
		os = []func() string{getWindows, getMacOs, getLinux}
	}

	return os[rand.Intn(len(os))]()
}

