func getRandomAgent(isMobile bool) string {
	userAgent := "Mozilla/5.0 (%PLAT%) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/%VER% Safari/537.36"
	versions := getChromeVersions()
	version := versions[rand.Intn(len(versions))]
	device := getRandomOs(isMobile)
	if isMobile {
		version = version + " Mobile"
	}
	userAgent = strings.ReplaceAll(userAgent, "%VER%", version)
	userAgent = strings.ReplaceAll(userAgent, "%PLAT%", device)

	return userAgent
}
